# Function to apply the odd-even algorithm
def apply_odd_even_algorithm(arr):
    new_array = []
    for number in arr:
        if number % 2 == 0:  # Check if the number is even
            new_number = number // 2
        else:  # Number is odd
            new_number = (number * 3) + 1
        new_array.append(new_number)
    return new_array

# Variable 'n' determines how many times to apply the algorithm
n = 937  # You can change 'n' to any desired value

for _ in range(n):
    numbers = apply_odd_even_algorithm(numbers)

# Print the final result
print(numbers)
# Write your code here :-)

total_sum = sum(numbers)

# Print the sum
print("Sum of all elements:", total_sum)
